def binary_search(A, x):
    lo = 0
    hi = len(A) - 1
    while lo <= hi:
        mid = (lo + hi) // 2
        if A[mid] == x:
            return mid
        elif A[mid] < x:
            hi = mid - 1
        else:
            lo = mid + 1
    return -1
